function populateTranslations(n){$.each(n,function(n,e){elements=$("."+n);for(var t=0;t<=elements.length;t++)void 0!=elements[t]&&(elements[t].innerHTML=e)})}function getLanguage(){var n=$("body > div").attr("data-lang");return n}function getTranslationObject(n,e){return console.log(n[e]),n[e]}$.ajax({type:"GET",url:"js/global_translations.json",dataType:"json"}).done(function(n){console.log("done"),populateTranslations(getTranslationObject(n,getLanguage()))}).fail(function(){console.log("fail")});